import { createChartContext } from "./CreateChartContext.js";
export const renderDailyChart = async () => {
    createChartContext('daily');
    const chart = document.getElementById('daily-chart');
    // @ts-ignore
    new Chart(chart, {
        type: 'bar',
        data: {
            labels: ['8am',
                '9am',
                '10am',
                '11am',
                '12pm',
                '1pm',
                '2pm',
                '3pm',
                '4pm',
                '5pm',
                '6pm'],
            datasets: [
                {
                    label: 'Visitas',
                    data: [22, 33, 15, 12, 1, 2, 11, 15, 2, 4, 5],
                    borderWidth: 1
                },
                {
                    label: 'Marcaciones',
                    data: [2, 5, 8, 3, 6, 25, 50, 22, 12, 8, 10],
                    borderWidth: 1
                }
            ]
        },
        options: {
            responsive: true,
            plugins: {
                legend: {
                    position: 'bottom'
                },
                title: {
                    display: true,
                    text: 'Registros de hoy'
                }
            },
            scales: {
                y: {
                    beginAtZero: true
                }
            }
        }
    });
};
