import { createChartContext } from "./CreateChartContext.js";
export const renderWeeklyChart = async () => {
    createChartContext('weekly');
    const chart = document.getElementById('weekly-chart');
    // @ts-ignore
    new Chart(chart, {
        type: 'bar',
        data: {
            labels: [
                'Lunes',
                'Martes',
                'Miércoles',
                'Jueves',
                'Viernes',
                'Sábado',
                'Domingo'
            ],
            datasets: [
                {
                    label: 'Visitas',
                    data: [129, 200, 153, 122, 133, 221, 113],
                    borderWidth: 1
                },
                {
                    label: 'Marcaciones',
                    data: [209, 100, 193, 102, 130, 201, 213],
                    borderWidth: 1
                }
            ]
        },
        options: {
            responsive: true,
            plugins: {
                legend: {
                    position: 'bottom'
                },
                title: {
                    display: true,
                    text: 'Registros de esta semana'
                }
            },
            scales: {
                y: {
                    beginAtZero: true
                }
            }
        }
    });
};
