import { createChartContext } from "./CreateChartContext.js";
export const renderYearlyChart = async () => {
    createChartContext('yearly');
    const chart = document.getElementById('yearly-chart');
    // @ts-ignore
    new Chart(chart, {
        type: 'bar',
        data: {
            labels: [
                'Ene',
                'Feb',
                'Mar',
                'Abr',
                'May',
                'Jun',
                'Jul',
                'Ago',
                'Sep',
                'Oct',
                'Nov',
                'Dic'
            ],
            datasets: [
                {
                    label: 'Visitas',
                    data: [22, 33, 15, 12],
                    borderWidth: 1
                },
                {
                    label: 'Marcaciones',
                    data: [2, 5, 8, 3],
                    borderWidth: 1
                }
            ]
        },
        options: {
            responsive: true,
            plugins: {
                legend: {
                    position: 'bottom'
                },
                title: {
                    display: true,
                    text: 'Registros de 2023'
                }
            },
            scales: {
                y: {
                    beginAtZero: true
                }
            }
        }
    });
};
